import React, { useState } from 'react';
import './SignIn.css';
import background from '../../assets/bg.svg';
import avatar from '../../assets/avatar.svg';
import wave from '../../assets/wave.png';
import { ToastContainer, toast } from 'react-toastify'; 
import 'react-toastify/dist/ReactToastify.css'; 
import { useNavigate } from 'react-router-dom'; 
import axios from 'axios'; 
import Validation from '../Validation/LoginValidation'; // Import the validation function

const SignIn = () => {
    const [values, setValues] = useState({
        email: '',
        password: '',
    });

    const [errors, setErrors] = useState({});
    const [userType, setUserType] = useState(''); // New state for user type
    const navigate = useNavigate(); 

    const handleChange = (e) => {
        setValues({
            ...values,
            [e.target.name]: e.target.value,
        });
    };

    const handleEmailChange = async (e) => {
        const email = e.target.value;
        setValues({ ...values, email });

        // Fetch user type based on the email
        if (email) {
            try {
                const response = await axios.get(`http://localhost:8087/userType?email=${email}`);
                if (response.data.userType) {
                    setUserType(response.data.userType); // Set the user type from response
                } else {
                    setUserType(''); // Reset if no user type found
                }
            } catch (error) {
                console.error('Error fetching user type:', error);
                toast.error('Failed to fetch user type.');
                setUserType(''); // Reset user type on error
            }
        } else {
            setUserType(''); // Reset if email input is empty
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const validationErrors = Validation(values);
        setErrors(validationErrors);

        if (validationErrors.email === "" && validationErrors.password === "") {
            try {
                const res = await axios.post('http://localhost:8087/signin', { ...values, userType }); // Send userType in request

                if (res.data.token) { // Check if token is present in response
                    localStorage.setItem('token', res.data.token); // Store the JWT token in local storage
                    toast.success('Login successful!'); 
                    navigate('/home'); 
                } else {
                    toast.error("No records existed"); 
                }
            } catch (err) {
                console.error('Error:', err); 
                toast.error("An error occurred. Please try again.");
            }
        }
    };

    return (
        <div>
            <img className="wave" src={wave} alt="wave" />
            <div className="container">
                <div className="img">
                    <h1 className="title1">SIGN IN</h1>
                    <img src={background} alt="background" />
                </div>
                <div className="login-content">
                    <form onSubmit={handleSubmit}>
                        <img src={avatar} alt="avatar" />
                        <h2 className="title">Welcome Back</h2>

                        <div className="input-div one">
                            <div className="i">
                                <i className="fas fa-envelope"></i>
                            </div>
                            <div className="div">
                                <input 
                                    type="email" 
                                    className="input" 
                                    placeholder='Email'
                                    name="email"
                                    value={values.email}
                                    onChange={handleEmailChange} // Update email change handler
                                />
                                {errors.email && <span className="error-message">{errors.email}</span>} {/* Display email error */}
                            </div>
                        </div>

                        <div className="input-div one">
                            <div className="i">
                                <i className="fas fa-user"></i>
                            </div>
                            <div className="div">
                                <select 
                                    className="input" 
                                    name="userType" 
                                    value={userType}
                                    onChange={(e) => setUserType(e.target.value)} // Handle dropdown change
                                >
                                    <option value="" disabled>Select User Type</option>
                                    {/* You could also add more user types dynamically based on your application */}
                                    <option value="admin">Admin</option>
                                    <option value="user">User</option>
                                    {/* Add more user types as needed */}
                                </select>
                            </div>
                        </div>

                        <div className="input-div pass">
                            <div className="i">
                                <i className="fas fa-lock"></i>
                            </div>
                            <div className="div">
                                <input 
                                    type="password" 
                                    className="input" 
                                    placeholder='Password'
                                    name="password"
                                    value={values.password}
                                    onChange={handleChange} 
                                />
                                {errors.password && <span className="error-message">{errors.password}</span>} {/* Display password error */}
                            </div>
                        </div>

                        <input type="submit" className="btn" value="Sign In" />
                        <a href='/signup' className="abtn">SIGN UP</a>
                        <p>Don't Have An Account?</p>
                    </form>
                </div>
            </div>
            <ToastContainer /> {/* Add ToastContainer for notifications */}
        </div>
    );
};

export default SignIn;
