import React, { useEffect, useState } from 'react';
import './UserActivities.css'; // Ensure this file is imported

const UserActivities = () => {
    const [activities, setActivities] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchUserActivities = async () => {
            try {
                const response = await fetch('http://localhost:8087/userActivities', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`, // assuming you store the JWT in local storage
                    }
                });

                if (!response.ok) {
                    throw new Error('Failed to fetch user activities');
                }

                const data = await response.json();
                setActivities(data);
            } catch (err) {
                setError(err.message);
            }
        };

        fetchUserActivities();
    }, []);

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div className="user-activities-container">
            <h2>User Activities</h2>
            <table className="activities-table">
                <thead>
                    <tr>
                        <th>User ID</th>
                        <th>Action</th>
                        <th>Timestamp</th>
                    </tr>
                </thead>
                <tbody>
                    {activities.map(activity => (
                        <tr key={activity.id}>
                            <td>{activity.user_id}</td>
                            <td>{activity.action}</td>
                            <td>{new Date(activity.timestamp).toLocaleString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default UserActivities;
