import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; 
import 'react-toastify/dist/ReactToastify.css';

function AdminHome() {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    profilePicture: null,
  });

  const [userInfo, setUserInfo] = useState({
    id: '',
    email: '',
    name: '',
  });

  const navigate = useNavigate(); 

  useEffect(() => {
    // Fetch user data from the API
    const fetchUserData = async () => {
      try {
          const token = localStorage.getItem('token'); // Get the JWT from local storage
  
          if (!token) {
              console.error('No token found, user is not authenticated.');
              navigate('/signin'); 
              return; 
          }
  
          const response = await fetch('http://localhost:8087/users', {
              method: 'GET',
              headers: {
                  Authorization: `Bearer ${token}`, // Include the token in the request headers
                  'Content-Type': 'application/json', 
              },
          });
  
          if (response.status === 403) {
              // Token is invalid or expired
              alert("Your session has expired, please log in again.");
              localStorage.removeItem('token'); // Optionally clear token from local storage
              navigate('/signin'); // Redirect to sign-in
              return; 
          }
  
          if (!response.ok) {
              console.error(`HTTP error! status: ${response.status}, statusText: ${response.statusText}`);
              throw new Error(`Network response was not ok: ${response.status} ${response.statusText}`);
          }
  
          const data = await response.json();
  
          setUserInfo({
              id: data.id, 
              email: data.email,
              name: data.name,
          });
  
          setFormData({
              fullName: data.name || '', 
              email: data.email || '', 
              profilePicture: null, 
          });
      } catch (error) {
          console.error('Error fetching user data:', error.message); 
      }
  };
  

    fetchUserData();
  }, [navigate]); 

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, profilePicture: e.target.files[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form Data:', formData);
  };

  const handleLogout = async () => {
    const confirmLogout = window.confirm("Are you sure you want to logout?");
    
    if (confirmLogout) {
        const token = localStorage.getItem('token');
        try {
            await fetch("http://localhost:8087/logout", {
                method: "POST",
                headers: {
                    "Authorization": `Bearer ${token}`,
                    "Content-Type": "application/json"
                },
            });
            localStorage.removeItem('token');  // Remove token from local storage
            console.log('User logged out');     // Logs logout action in console
            navigate('/');                      // Navigate to home page
        } catch (error) {
            console.error("Logout failed:", error); // Log error if logout fails
        }
    } else {
        console.log('Logout canceled');          // Logs cancel action in console
    }
};


  return (
    <div className='home'><p>{userInfo.name}Admin Home</p>
      <div className="app-container">
        <div className="profile-card">
          <div className="profile-image">
            <img
              src={formData.profilePicture ? URL.createObjectURL(formData.profilePicture) : "https://via.placeholder.com/150"}
              alt="Profile"
            />
          </div>
          <h2>{formData.fullName}</h2>
          <div className="user-info">
            <p className='user_data'>User ID: {userInfo.id}</p>
            <p className='user_data'>Name: {userInfo.name}</p>
            <p className='user_data'>Email: {userInfo.email}</p>
          </div>
          <div className="buttons">
            <button className="logout-btn" onClick={handleLogout}>Logout</button>
          </div>
        </div>

        <div className="edit-profile-form">
          <h2 className='home_h1'>Edit Profile</h2>
          <form onSubmit={handleSubmit}>
            <label>Full Name</label>
            <input 
              type="text" 
              name="fullName"
              className='home_input' 
              value={formData.fullName} 
              onChange={handleChange} 
            />
            
            <label>Email</label>
            <input 
              type="text"
              name="email"
              className='home_input' 
              value={formData.email} 
              onChange={handleChange} 
            />
            
            <label>Profile Picture</label>
            <input 
              type="file" 
              name="profilePicture" 
              className='home_input' 
              onChange={handleFileChange} 
            />
            
            <button type="submit" className="update-btn">Update</button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AdminHome;
